// Kitchen Sink Generator Util
const kitchenSinkTransactions: AssetTransactionType[] = [];
Object.values(AssetTransactionCategory).forEach((category) => {
  Object.values(AssetTransactionStatus).forEach((status) => {
    kitchenSinkTransactions.push({
      ...baseTransaction,
      category,
      status,
    });
    if (category === AssetTransactionCategory.Receipt || category === AssetTransactionCategory.Deposit) {
      kitchenSinkTransactions.push({
        ...baseTransaction,
        category,
        sender: undefined,
        receiver: undefined,
        status,
      });
    }
  });
});

export const KitchenSink = Template.bind({});
KitchenSink.args = {
  transactions: kitchenSinkTransactions,
};
